mapper.select:
select id, user_name userName, user_password userPassword, user_email userEmail, user_info userInfo, head_img headImg, create_time createTime from sys_user
and user_name like concat('%',#{userName},'%')
and user_email = #{userEmail}

select id, user_name userName, user_password userPassword, user_email userEmail, user_info userInfo, head_img headImg, create_time createTime from sys_user where 1 = 1
and id =#{id}
and user_name = #{userName}
and 1 = 2

mapper.insert:
insert into sys_user(
                     id,user_name,user_password, user_email,
                     user_info, head_img, create_time
)values(
        #{id}, #{userName}, #{userPassword}, #{userEmail},
        #{userInfo}, #{headImg, jdbcType=BLOB}, #{createTime, jdbcType= TIMESTAMP}
               )

insert into sys_user(
    user_name,user_password,

        user_email,

    user_info, head_img, create_time
)values(
           #{userName}, #{userPassword},

               #{userEmail},

           #{userInfo}, #{headImg, jdbcType=BLOB}, #{createTime, jdbcType= TIMESTAMP}
       )

insert into sys_user(
    user_name,user_password, user_email,
    user_info, head_img, create_time
)values(
           #{userName}, #{userPassword}, #{userEmail},
           #{userInfo}, #{headImg, jdbcType=BLOB}, #{createTime, jdbcType= TIMESTAMP}
       )

    SELECT LAST_INSERT_ID()
